import { ProducerService } from './../../services/producer.service';
import { trigger, state, style, animate, transition } from '@angular/animations';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { ColumnMode, SelectionType } from '@swimlane/ngx-datatable';
import { ContractorMaterialService } from './../../services/conctractor-material.service';
import { ContractorMaterial } from './../models/contractorMaterials';
import { Page } from 'src/app/models/page';
import { Component, OnInit } from '@angular/core';
import { Producer } from '../models/producers';

@Component({
  selector: 'app-contractor-materials',
  templateUrl: './contractor-materials.component.html',
  styleUrls: ['./contractor-materials.component.scss'],
  animations: [
    trigger('expandedPanel', [
      state('open', style({
        'margin-top': '50px',
        opacity: 1,
      })),
      state('closed', style({
        'margin-top': '-1000px',
        opacity: 0
      })),
      transition('closed => open', [
        animate('0.4s')
      ]),
      transition('* => open', [
        animate('0.4s')
      ]),
      transition('open => closed', [
        animate('0.4s')
      ]),
      transition('* => closed', [
        animate('0.4s')
      ])
    ]),
  ]
})
export class ContractorMaterialsComponent implements OnInit {

  public ngxDataTableProperties: any = {
    columns: new Array<any>,
    rows: new Array<ContractorMaterial>,
    loading: false,
    page: new Page()
  };
  public selectedItem: ContractorMaterial | undefined = undefined;
  public ColumnMode = ColumnMode;
  public SelectionType = SelectionType;
  public selected = new Array<any>;
  public addVisibility: boolean = false;
  public addAnimationState: string = "close";
  public form!: FormGroup;
  public searchProducers: Array<Producer> = [];
  public AddSelectedProducer: Producer | undefined;

  constructor(private materialService: ContractorMaterialService, private router: Router, private route: ActivatedRoute, private formBuilder:FormBuilder, private producerService: ProducerService) { }

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      switch (params['type']) {
        case 'add':
          this.generateAddForm();
          break;
        // case 'edit':
        //   this.generateEditForm();
        //   break;
        default:
          this.router.navigate(['contractorMaterials']);
      }
    });
    this.setPage({ offset: 0 });
  }

  public setPage(event: any) {
    this.ngxDataTableProperties.loading = true;
     this.materialService.GetAll(event.offset).subscribe(pagedData => {
       this.generateColumns(Object.keys(pagedData.data[0]));
       this.ngxDataTableProperties.page = pagedData.page;
       this.ngxDataTableProperties.rows = pagedData.data;
       this.ngxDataTableProperties.loading = false;
     });
   }

   public selectProducer(item: Producer) {
    this.AddSelectedProducer = item;
   }

   public getOne(id: number) {
    // this.materialService.Get(id).subscribe(data => {
    //   this.selectedProducer = data;
    //   this.router.navigate(['producers'], {queryParams: {type: 'edit'}})
    // })
    this.materialService.Get(id).subscribe(data => {
      console.log(data);
      this.selectedItem = data;
      this.router.navigate(['contractorMaterials'], {queryParams: {type: 'edit'}});
    });
  }

  public Create() {
    this.materialService.Create({...this.form.value, producer: { ...this.AddSelectedProducer } }).subscribe(data => {
      this.router.navigate(['contractorMaterials']);
    });
  }

  public checkClick(event: Event) {
    const target = event.target as HTMLElement;
    if(target.classList.contains('add-background')) {
      this.closeAdd();
    }
    // if(target.classList.contains('edit-background')) {
    //   this.closeEdit();
    // }
  }

  public onSelect(event: any) {
    if(this.selected[0] != undefined) {
      this.getOne(this.selected[0].id)
    }
    this.selected = new Array<any>;
  }

  public onSort(event: any) {

  }

  public toShortDate(value: string) {
    return value.split('T')[0];
  }

  public onInputProducer(value: string) {
    this.AddSelectedProducer = undefined;
    if(value.length > 2) {
      this.producerService.GetListSearch(value).subscribe(data => {
        this.searchProducers = data.producers;
      });
    }
    else {
      this.searchProducers = [];
    }
  }


   private generateColumns(columns: Array<string>) {
    this.ngxDataTableProperties.columns = [];
    columns.forEach(item => {
      this.ngxDataTableProperties.columns.push({name: item, prop: item});
    });
  }

  private closeAdd() {
    this.addAnimationState = 'close';
    this.addVisibility = false;
    this.router.navigate(['contractorMaterials']);
  }

  private closeEdit() {
    // this.editVisibility = false;
    // this.router.navigate(['producers']);
  }

  private generateAddForm() {
    this.addVisibility = true;
    this.addAnimationState = "open";
    this.form = this.formBuilder.group({
        "name" : ['', [Validators.required, Validators.maxLength(100)]],
        "createdDate" : ['', [Validators.required]],
        "validaty": ['', [Validators.required]]
    });
  }
}
<div class="wrapper">

  <header>
    <app-header (menuShow)="nav.togleShow()"></app-header>
  </header>

  <main>
               <ngx-datatable
               class="material"
              [rows]="ngxDataTableProperties.rows"
              [columnMode]="ColumnMode.force"
              [headerHeight]="50"
              [footerHeight]="50"
              rowHeight="auto"
              [externalPaging]="true"
              [externalSorting]="true"
              [count]="ngxDataTableProperties.page.totalElements"
              [offset]="ngxDataTableProperties.page.pageNumber"
              [limit]="ngxDataTableProperties.page.size"
              [loadingIndicator]="ngxDataTableProperties.loading"
              (page)="setPage($event)"
              (sort)="onSort($event)"
              [selected]="selected"
              [selectionType]="SelectionType.single"
              (click)="onSelect($event)"
             >
             <ngx-datatable-column name="#" prop="id"></ngx-datatable-column>
             <ngx-datatable-column name="Name" prop="name"></ngx-datatable-column>

             <ngx-datatable-column name="CreatedDate">
              <ng-template let-column="column" ngx-datatable-header-template> {{ column.name }} </ng-template>
              <ng-template let-value="value" ngx-datatable-cell-template>
                {{toShortDate(value)}}
              </ng-template>
            </ngx-datatable-column>

            <ngx-datatable-column name="Validaty">
              <ng-template let-column="column" ngx-datatable-header-template> {{ column.name }} </ng-template>
              <ng-template let-value="value" ngx-datatable-cell-template>
                {{toShortDate(value)}}
              </ng-template>
            </ngx-datatable-column>

            <ngx-datatable-column name="Producer">
              <ng-template let-column="column" ngx-datatable-header-template> {{ column.name }} </ng-template>
              <ng-template let-value="value" ngx-datatable-cell-template>
                {{value.name}}
              </ng-template>
            </ngx-datatable-column>

             </ngx-datatable>

    <div class="nav-container">
      <nav>
        <a [routerLink]="'/contractorMaterials'" [queryParams]="{type: 'add'}">Добавить</a>
      </nav>
    </div>

  </main>

</div>


<div class="add-wrapper add-background" *ngIf="addVisibility" (click)="checkClick($event)">
  <div class="container" [formGroup]="form" [@expandedPanel]="addAnimationState">
   <h1>Добавление</h1>
   <label>Name: <input type="text" formControlName="name"></label>
   <label>CreatedDate: <input type="date" formControlName="createdDate"></label>
   <label>Validaty: <input type="date" formControlName="validaty"></label>
   <label>Producer: <input type="text" (input)="onInputProducer(inputProducer.value)" #inputProducer [value]="AddSelectedProducer?.name"></label>
    <div class="backdrop-producer" *ngIf="searchProducers.length > 0 && AddSelectedProducer == undefined">
      <p *ngFor="let item of searchProducers" (click)="selectProducer(item)">{{ item.name }}</p>
    </div>
   <button [disabled]="form.invalid" (click)="Create()">Сохранить</button>
  </div>
</div>

<div class="edit-wrapper edit-background" (click)="checkClick($event)">
  <div class="container">
    <label>Name: <input type="text"></label>
    <label>CreatedDate: <input type="date"></label>
    <label>Validaty: <input type="date"></label>
    <label>Producer: <input type="text" (input)="onInputProducer(inputProducer.value)" #inputProducer [value]="AddSelectedProducer?.name"></label>
    <mat-autocomplete #auto="matAutocomplete" [displayWith]="onIn">
      <mat-option *ngFor="let option of filteredOptions | async" [value]="option">
        {{option.name}}
